import React, { useEffect, useState } from "react";
import "./App.css";
import Wrapper from "./components/Wrapper";
import Navbar from "./components/Navbar";
import SearchForm from "./components/SearchForm";
import Container from "./components/Container";
import Table from "./components/Table";
import Alert from "./components/Alert";

function App() {
  const [modalShow, setModalShow] = useState(false);
  const employeeData = [
    {
      name: "Norman Lei",
      gender: "Male",
      email: "normanlei0901@gmail.com",
      phone: "(206)349-3478",
      department: "Full-Stack"
    },
    {
      name: "Wenfu Lei",
      gender: "Male",
      email: "normanlei@gmail.com",
      phone: "(206)349-3477",
      department: "Full-Stack"
    },
    {
      name: "Yvonne Chen",
      gender: "Female",
      email: "xinyachen@gmail.com",
      phone: "(206)388-3478",
      department: "Front-end"
    },
    {
      name: "Xinya Chen",
      gender: "Female",
      email: "yvonnechen@gmail.com",
      phone: "(206)529-3478",
      department: "Back-end"
    },
    {
      name: "Mike Jack",
      gender: "Male",
      email: "jackmike@gmail.com",
      phone: "(206)349-9191",
      department: "Front-end"
    },
  ];
  const columns = React.useMemo(
    () => [
      {
        Header: 'Name',
        accessor: 'name',

      },

      {
        Header: 'Gender',
        accessor: 'gender',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Phone',
        accessor: 'phone',
      },
      {
        Header: 'Department',
        accessor: 'department',
      },
    ],
    []
  );
  const [employees, setEmployees] = useState(employeeData);
  const [data, setData] = React.useState({
    search: "",
    category: "name",
    error: ""
  });

  useEffect(() => {
    if (!data.search) return;
    const newEmployees = employees.filter(employee =>
      employee[data.category].includes(data.search)
    );
    setEmployees(newEmployees);
  }, []);

  const handleInputChange = event => {
    setData({ search: event.target.value });
  };

  const handleOptionChange = event => {
    setData({ category: event.target.value });
  };

  const handleFormSubmit = event => {
    event.preventDefault();
    if (!data.search) {
      return;
    }
    const newEmployees = employees.filter(employee =>
      employee[data.category].includes(data.search)
    );
    setEmployees(newEmployees);
  };

  return (
    <Wrapper>
      <Navbar modalShow={modalShow} setModalShow={setModalShow}></Navbar>
      <Container>
        <SearchForm
          data={data}
          setData={setData}
          handleFormSubmit={handleFormSubmit}
          handleInputChange={handleInputChange}
          handleOptionChange={handleOptionChange}
        ></SearchForm>
        <Alert type="danger" style={{ opacity: data.error ? 1 : 0, marginBottom: 10 }}>
          {data.error}
        </Alert>
        <Table columns={columns} data={employees}></Table>
      </Container>
    </Wrapper>
  );
}


export default App;
